prompt prompt final blockers from gv$session (all events)
select final_blocking_instance f_blk_inst, final_blocking_session f_blk_sess, event, sql_id, row_wait_obj#||','||row_wait_block# obj_lck, count(*) num_blocked, max(wait_time_micro) max_wait_musec from gv$session where final_blocking_session_Status='VALID' group by final_blocking_instance, final_blocking_session, event, sql_id, row_wait_obj#||','||row_wait_block# order by 1;
