PRO ASH REPORT for last 1 hour SET LINES 200 PAGES 100
col timed_event for a60 trunc col samples for 999999 col percent for 99.9 col dummy_01 for a20
WITH events AS ( SELECT /*+ MATERIALIZE NO_MERGE */ CASE h.session_state WHEN 'ON CPU' THEN h.session_state ELSE h.wait_class||' "'||h.event||'"' END timed_event, COUNT(*) samples FROM gv$active_session_history h WHERE 'Y' = 'Y' AND CAST(h.sample_time AS DATE) > SYSDATE - 1/24 GROUP BY CASE h.session_state WHEN 'ON CPU' THEN h.session_state ELSE h.wait_class||' "'||h.event||'"' END ORDER BY 2 DESC ), total AS ( SELECT SUM(samples) samples, SUM(CASE WHEN ROWNUM > 15 THEN samples ELSE 0 END) others FROM events ) SELECT e.timed_event, e.samples, ROUND(100 * e.samples / t.samples, 1) percent, NULL dummy_01 FROM events e, total t WHERE ROWNUM <= 15 AND ROUND(100 * e.samples / t.samples, 1) > 0.1 UNION ALL SELECT 'Others', others samples, ROUND(100 * others / samples, 1) percent, NULL dummy_01 FROM total WHERE others > 0 AND ROUND(100 * others / samples, 1) > 0.1;
PRO TOP SQLs col sql_id for a20 col sql_text for a100 trunc
WITH events AS ( SELECT /*+ MATERIALIZE NO_MERGE */ NVL(h.sql_id, 'null') sql_id, COUNT(*) samples FROM gv$active_session_history h WHERE 'Y' = 'Y' AND CAST(h.sample_time AS DATE) > sysdate - 1/24 AND sql_id IS NOT NULL GROUP BY h.sql_id ORDER BY 2 DESC ), total AS ( SELECT SUM(samples) samples, SUM(CASE WHEN ROWNUM > 15 THEN samples ELSE 0 END) others FROM events ) SELECT e.sql_id, e.samples, ROUND(100 * e.samples / t.samples, 1) percent, NULL sql_text --(SELECT v2.sql_text FROM gv$sql v2 WHERE v2.sql_id = e.sql_id AND ROWNUM = 1) sql_text FROM events e, total t WHERE ROWNUM <= 15 AND ROUND(100 * e.samples / t.samples, 1) > 0.1 UNION ALL SELECT 'Others', others samples, ROUND(100 * others / samples, 1) percent, NULL sql_text FROM total WHERE others > 0 AND ROUND(100 * others / samples, 1) > 0.1;

declare -- lv_tabs number:=0; -- procedure get_privs (pv_grantee in varchar2,lv_tabstop in out number) is --DECLARE -- pv_grantee VARCHAR2(100) := 'RTA'; -- lv_tabstop NUMBER := 3; lv_tab VARCHAR2(50) := ''; lv_loop NUMBER; -- CURSOR c_main ( cp_grantee IN VARCHAR2 ) IS SELECT 'ROLE' typ, grantee grantee, granted_role priv, admin_option ad, '--' tabnm, '--' colnm, '--' owner FROM dba_role_privs WHERE grantee = cp_grantee -- AND granted_role NOT IN ( 'CONNECT', 'SELECT_CATALOG_ROLE', 'EXECUTE_CATALOG_ROLE', 'RESOURCE', 'DBA', -- 'IMP_FULL_DATABASE', 'EXP_FULL_DATABASE', 'AQ_ADMINISTRATOR_ROLE' ) UNION SELECT 'SYSTEM' typ, grantee grantee, privilege priv, admin_option ad, '--' tabnm, '--' colnm, '--' owner FROM dba_sys_privs WHERE grantee = cp_grantee UNION SELECT 'TABLE' typ, grantee grantee, privilege priv, grantable ad, table_name tabnm, '--' colnm, owner owner FROM dba_tab_privs WHERE grantee = cp_grantee UNION SELECT 'COLUMN' typ, grantee grantee, privilege priv, grantable ad, table_name tabnm, column_name colnm, owner owner FROM dba_col_privs WHERE grantee = cp_grantee ORDER BY 1;
BEGIN lv_tabstop := lv_tabstop + 1; FOR lv_loop IN 1..lv_tabstop LOOP lv_tab := lv_tab || chr(9); END LOOP;
FOR lv_main IN c_main(pv_grantee) LOOP IF lv_main.typ = 'ROLE' --and lv_main.priv NOT IN ( 'CONNECT', 'SELECT_CATALOG_ROLE', 'EXECUTE_CATALOG_ROLE', 'RESOURCE', 'DBA', -- 'IMP_FULL_DATABASE', 'EXP_FULL_DATABASE', 'AQ_ADMINISTRATOR_ROLE' ) THEN dbms_output.put_line(lv_tab || 'ROLE => ' || lv_main.priv || ' which contains =>'); get_privs(lv_main.priv,lv_tabstop); ELSIF lv_main.typ = 'SYSTEM' THEN dbms_output.put_line(lv_tab || 'SYS PRIV => ' || lv_main.priv || ' grantable => ' || lv_main.ad); ELSIF lv_main.typ = 'TABLE' THEN dbms_output.put_line(lv_tab || 'TABLE PRIV => ' || lv_main.priv || ' object => ' || lv_main.owner || '.' || lv_main.tabnm || ' grantable => ' || lv_main.ad); ELSIF lv_main.typ = 'COLUMN' THEN dbms_output.put_line(lv_tab || 'COL PRIV => ' || lv_main.priv || ' object => ' || lv_main.tabnm || ' column_name => ' || lv_main.owner || '.' || lv_main.colnm || ' grantable => ' || lv_main.ad); END IF; END LOOP;
lv_tabstop := lv_tabstop - 1; lv_tab := ''; EXCEPTION WHEN OTHERS THEN dbms_output.put_line('ERROR (get_privs) => ' || sqlcode); dbms_output.put_line('MSG (get_privs) => ' || sqlerrm); END get_privs;
begin DBMS_OUTPUT.ENABLE(NULL); dbms_output.put_line('User => '||upper(:USERNAME)||' has been granted the following privileges'); dbms_output.put_line('*********************************************************'); get_privs(upper(:USERNAME),lv_tabs); exception when others then dbms_output.put_line('ERROR (main) => '||sqlcode); dbms_output.put_line('MSG (main) => '||sqlerrm);
end;
